import Repository from './Repository';

const resource = "/user";

export default {
    get(){
        Repository.get(resource);
    },

    getUser(userId){
        Repository.get(resource + "/" + userId);
    },

    createPost(payload){
        Repository.post(resource, payload);
    },

    updateUser(userId, payload){
        Repository.put(resource + "/" + userId, payload);
    },

    register(payload){
        return Repository.post(resource + "/register", payload);
    },
    
    login(payload){
        return Repository.post(resource + "/login", payload);
    },

    createMentor(userId, payload){
        return Repository.post(resource + "/mentor", payload);
    }
}
